import Card from "../component/Card";
import React, { useCallback, useEffect, useState } from "react";
import axios from "axios";
import Loading from "./Loading";

function CardContaioner() {
  const [newData, setData] = useState([[1]]);
  const [poster, setPoster] = useState({});
  const [title, setTitle] = useState([]); // 1~10위 제목이 들어감
  const [isLoading, setLoading] = useState(false);

  const getPoster = useCallback(async (el, idx) => {
    setLoading(true);

    try {
      const response = await axios.get(
        `http://localhost:4000/naver/?query=${el}}`
      );
      const data = await response.data;

      // 인덱스 설정 후 사진 순위대로 가져오기 구현 해야함
      await poster.push({ idx: data[0].image });

      // setPoster(poster.concat([el, idx, data[0].image]));
      //  poster.push([data[0].image]);
      console.log(poster);
      setLoading(false);
    } catch (error) {
      console.log(error);
    }
  }, []);

  const getData = useCallback(async () => {
    try {
      setLoading(true);
      const response = await axios.get("http://localhost:4000/kobis");
      const data = await response.data;
      setData(data);
      console.log(data);
      setLoading(false);
    } catch (error) {
      console.log(error);
    }
  }, []);

  const getTitle = useCallback(async () => {
    setLoading(true);
    try {
      const response = await axios.get("http://localhost:4000/kobis/title");
      const data = await response.data;
      console.log(data);
      setTitle(data);
      // setLoading(false);
      title.map((el) => {
        getPoster(el, title.indexOf(el));
      });
    } catch (error) {
      console.log(error);
    }
  }, []);

  useEffect(() => {
    getTitle();
    getData();
  }, [setLoading]);

  return (
    <>
      {isLoading ? <Loading /> : null}
      <div className="header"></div>
      <div className="title-wrap">
        <div className="title">오늘의 영화 순위</div>
      </div>
      <section className="slider-section">
        <div className="wheel">
          {isLoading === false ? (
            <Card newData={newData} poster={poster}></Card>
          ) : null}
        </div>
      </section>
      <div className="title-wrap">
        <div className="scroll-down">
          Scroll down
          <div className="arrow"></div>
        </div>
      </div>
    </>
  );
}

export default React.memo(CardContaioner);
